<?phpif (!defined('sugarEntry') || !sugarEntry)    die('Not A Valid Entry Point');require_once('custom/modules/Schedulers/license/OutfittersLicense.php');require_once("modules/Administration/Administration.php");$job_strings[] = 'importCacheEmails';function importCacheEmails(){	require('custom/modules/Schedulers/license/config.php');    if (OutfittersLicense::validateModule()) {        $admin = new Administration();        $admin->retrieveSettings();        if (isset($admin->settings[$outfitters_config['shortname'].'_validated_users']) && $admin->settings[$outfitters_config['shortname'].'_validated_users'] == true) {            set_time_limit(9000);            ini_set('memory_limit', '2048M'); //blacklist while package scan            require_once('custom/include/CacheEmails/CacheEmails.php');            return CacheEmails::import_emails();        } else {            $GLOBALS['log']->fatal('Email Archiving failed in CRON run. You enabled more than allowed users for GSync');            return false;        }    } else {        $GLOBALS['log']->fatal('Email Archiving failed in CRON run. Unable to validate key.');        return false;    }}